mapper.select:
SELECT p.product_id, p.product_name, p.product_desc, p.img_addr, p.normal_price, p.promotion_price, p.priority, p.create_time, p.last_edit_time, p.enable_status, p.product_category_id, p.shop_id, pc.product_category_id, pc.product_category_name FROM tb_product AS p LEFT JOIN tb_product_category AS pc ON p.product_category_id = pc.product_category_id
p.enable_status != -1
AND p.shop_id = #{productCondition.shop.shopId}
AND p.shop_id = #{productCondition.shop.shopId}
and p.product_category_id = #{productCondition.productCategory.productCategoryId}
and p.product_name like '%${productCondition.productName}%'
and p.enable_status = #{productCondition.enableStatus}
ORDER BY priority DESC LIMIT #{pageSize} OFFSET #{rowIndex};

SELECT count(1)
FROM tb_product

    enable_status != -1

        AND shop_id = #{productCondition.shop.shopId}


        and product_category_id =
        #{productCondition.productCategory.productCategoryId}


        and product_name like '%${productCondition.productName}%'


        and enable_status = #{productCondition.enableStatus}

mapper.update:
UPDATE tb_product
product_name=#{productName}, product_desc=#{productDesc}, img_addr=#{imgAddr}, normal_price=#{normalPrice}, promotion_price=#{promotionPrice}, priority=#{priority}, last_edit_time=#{lastEditTime}, enable_status=#{enableStatus},
product_category_id=#{productCategory.productCategoryId}
WHERE product_id = #{productId} AND shop_id = #{shop.shopId}

mapper.delete:
DELETE FROM
tb_product
WHERE
product_id = #{productId}
AND
shop_id=#{shopId};
